import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Coin from './components/Coin';
import NavBar from './components/NavBar';
import './App.css';
import Placeholder from './components/Placeholder';
let curDisplay;
let curLink;
let menu;

function App() {

  if(menu == "USD"){
    curDisplay = '$ ';
    curLink = "usd";
  }
  else if(menu == "CZK"){
    curDisplay = "CZK ";
    curLink = "czk";
  }
  else{
    curDisplay = "$ "
    curLink = "usd"
  }

  const [coins, setCoins] = useState([])
  const [search, setSearch] = useState('')

  const changeHandler = e => {
    setSearch(e.target.value)
  }

  const coinFilter = coins.filter(coin =>
    coin.name.toLowerCase().includes(search.toLowerCase())
  )

  useEffect(() => {
    axios.get(`https://api.coingecko.com/api/v3/coins/markets?vs_currency=${curLink}&order=market_cap_desc&per_page=100&page=1&sparkline=false`)
      .then(res => {
        setCoins(res.data);
      }).catch(err => {
        console.log(err);
        alert('Error');
      });
  }, []);



  return (
    <div className="container">
      <div className="nav">
        <NavBar />
        <div className="search">
        <select
                onChange={(e) => {
                    const selected = e.target.value;
                    menu = selected;
                    console.log(menu);
                    window.location.reload();
                    console.log(menu);
                }}
            >
                <option value="USD">USD</option>
                <option value="CZK">CZK</option>
            </select>
          <form>
            <input type="text" placeholder="Search" className="search-input" onChange={changeHandler} />
          </form>
        </div>
      </div>
      <Placeholder />
      {coinFilter.map(coin => {
        return ( 
          <Coin
            key={coin.id}
            coinName={coin.name}
            icon={coin.image}
            symbol={coin.symbol}
            volume={coin.total_volume}
            price={coin.current_price}
            priceChange24={coin.price_change_percentage_24h}
            marketcap={coin.market_cap}
            cur={curDisplay}
          />
        );
      })}
    </div>
  );
}


export default App;
