import React, {useState, useEffect}from 'react';
import axios from 'axios';
import Coin from './components/Coin';
import './App.css';

function App() {

  const [coins, setCoins] = useState([])
  const [search, setSearch] = useState('')

  useEffect(()=>{
    axios.get('https://api.coingecko.com/api/v3/coins/markets?vs_currency=czk&order=market_cap_desc&per_page=100&page=1&sparkline=false')
    .then(res =>{
      setCoins(res.data);
    }).catch(err=>{
      console.log(err);
      alert('Error');
    });
  },[]);

  const changeHandler = e =>{
    setSearch(e.target.value)
  }

  const coinFilter = coins.filter(coin=>{
    coin.name.toLowerCase().includes(search.toLowerCase())
  })


  return (
    <div className="container">
      <div className="search">
        <h1 className="search-text">Search</h1>
        <form>
          <input type="text" placeholder="search" className="search-input" onChange={changeHandler}/>
        </form>
      </div>
      {coinFilter.map(coin => {
        return
          <Coin 
            key={coin.id} 
            coinName={coin.name} 
            icon={coin.image} 
            symbol={coin.symbol}
            volume={coin.market_cap}
            price={coin.current_price}
          />
      })}
    </div>
  );
}

export default App;
