import React from 'react'
import '../styles/coin.css'

const coin = ({ icon, coinName, symbol, price, volume, priceChange24, priceChange7d, marketcap, cur }) => {
    return (
        <div className="coin-container">
            <div className="coin-row">
                <div className="coin">
                    <img src={icon} alt="icon"/>
                    <h1>{coinName}</h1>
                    <p className="coin-symbol">{symbol.toLocaleString().toUpperCase()}</p>
                </div>
                <div className="coin-data">
                    <p className="coin-price">{cur}{price}</p>
                    <p className="coin-volume">{cur}{volume.toLocaleString()}</p>
                    {priceChange24 < 0 ? (
                        <p className="coin-perc-red">{priceChange24.toFixed(2)}%</p>
                    ) : (
                        <p className="coin-perc-green">{priceChange24.toFixed(2)}%</p>
                    )}
                    {priceChange7d < 0 ? (
                        <p className="coin-perc-red">{priceChange7d.toFixed(2)}%</p>
                    ) : (
                        <p className="coin-perc-green">{priceChange7d.toFixed(2)}%</p>
                    )}
                    <p className="coin-marketcap">
                        Mkt cap : {cur}{marketcap.toLocaleString()}
                    </p>
                </div>
            </div>
        </div>
    )
}

export default coin
