import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Coin from './components/Coin';
import logo from './icons/logo.png';
import './App.css';
const curDisplay = "$"
const curLink = "usd"


function App() {

  const [coins, setCoins] = useState([])
  const [search, setSearch] = useState('')

  const changeHandler = e => {
    setSearch(e.target.value)
  }

  const coinFilter = coins.filter(coin =>
    coin.name.toLowerCase().includes(search.toLowerCase())
  )

  useEffect(() => {
    axios.get(`https://api.coingecko.com/api/v3/coins/markets?vs_currency=${curLink}&order=market_cap_desc&per_page=100&page=1&sparkline=false`)
      .then(res => {
        setCoins(res.data);
      }).catch(err => {
        console.log(err);
        alert('Error');
      });
  }, []);

  var icon = (
    <span class="logo">
      <a href="/">
        <img src={logo} alt="LOGO" />
      </a>
    </span>
  );

  return (
    <div className="container">

      <div className="nav">
        <Navbar brand={icon}/>
        <div className="search">
          <h1 className="site-name">Crypto Calculator</h1>
          <form>
            <input type="text" placeholder="search" className="search-input" onChange={changeHandler} />
          </form>
        </div>
      </div>
      {coinFilter.map(coin => {
        return (
          <Coin
            key={coin.id}
            coinName={coin.name}
            icon={coin.image}
            symbol={coin.symbol}
            volume={coin.total_volume}
            price={coin.current_price}
            priceChange={coin.price_change_percentage_24h}
            marketcap={coin.market_cap}
            cur={curDisplay}
          />
        );
      })}
    </div>
  );
}


export default App;
