import React,{ useState } from 'react'
import '../App.css';
import { SiBitcoin } from "react-icons/si";
import { IoMdHeart } from "react-icons/io";
import { IoMdHeartEmpty } from "react-icons/io";
import { AiOutlineStock } from "react-icons/ai";
import '../styles/Navbar.css'

const [style, setstyle] = useState(initialState)

const NavBar = (props) => {
    return (
        <div className="navbar">
            <div className="logo">
                <SiBitcoin />
                <span>CryptoCalc</span>
            </div>
            <div className="Bookmarks btn" style={style} onClick={() => {
                props.bookmode();
                if (props.changestyle()) {
                    style = {
                        borderBottom: "2px solid #fff",
                        borderRadius: "5px",
                        backgroundColor: "rgba(50, 50, 53, 0.322)"
                    }
                }
                console.log(style);
            }}>
                {(() => {
                    if (props.book === true) {
                        return (
                            <IoMdHeart color={"red"} />
                        )
                    } else {
                        return (
                            <IoMdHeartEmpty color={"red"} />
                        )
                    }
                })()}
                <span>Favourites</span>
            </div>
            <div className="investMark btn" onClick={props.investmode}>

                {(() => {
                    if (props.invested === true) {
                        return (
                            <AiOutlineStock color={"green"} />
                        )
                    } else {
                        return (
                            <AiOutlineStock color={"white"} />
                        )
                    }
                })()}
                <span>Calculation</span>
            </div>
            <div className="search">
                <input type="text" placeholder="Search" className="search-input" onChange={props.changeHandler} />
            </div>
        </div>
    )
}

export default NavBar
